.quick-nav {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
  margin: 2rem 0;
}

.nav-card {
  background: var(--highlight);
  border-radius: 12px;
  padding: 1.5rem;
  border: 1px solid var(--secondary);
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.nav-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(0,0,0,0.1);
}

.nav-card h3 {
  margin: 0 0 0.5rem 0;
  color: var(--secondary);
}

.nav-card p {
  margin: 0 0 1rem 0;
  color: var(--darkgray);
  font-size: 0.9rem;
}

.nav-link {
  display: inline-block;
  color: var(--secondary);
  text-decoration: none;
  font-weight: 500;
  transition: color 0.2s ease;
}

.nav-link:hover {
  color: var(--tertiary);
}

.stats-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
  gap: 1rem;
  margin: 2rem 0;
}

.stat-item {
  text-align: center;
  padding: 1rem;
  background: var(--light);
  border-radius: 8px;
  border: 1px solid var(--lightgray);
}

.stat-number {
  font-size: 2rem;
  font-weight: bold;
  color: var(--secondary);
  margin-bottom: 0.5rem;
}

.stat-label {
  font-size: 0.9rem;
  color: var(--darkgray);
}

.popular-terms {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin: 1.5rem 0;
}

.term-tag {
  display: inline-block;
  padding: 0.5rem 1rem;
  background: var(--secondary);
  color: white;
  text-decoration: none;
  border-radius: 20px;
  font-size: 0.9rem;
  transition: background-color 0.2s ease;
}

.term-tag:hover {
  background: var(--tertiary);
  color: white;
}

.recent-updates {
  margin: 2rem 0;
}

.update-item {
  display: flex;
  align-items: center;
  padding: 0.75rem 0;
  border-bottom: 1px solid var(--lightgray);
}

.update-item:last-child {
  border-bottom: none;
}

.update-date {
  font-size: 0.8rem;
  color: var(--gray);
  min-width: 100px;
  margin-right: 1rem;
}

.update-content {
  color: var(--darkgray);
}

.footer-note {
  margin-top: 3rem;
  padding: 1.5rem;
  background: var(--highlight);
  border-radius: 8px;
  text-align: center;
}

.footer-note p {
  margin: 0.5rem 0;
  font-size: 0.9rem;
  color: var(--darkgray);
}

.footer-note a {
  color: var(--secondary);
  text-decoration: none;
}

.footer-note a:hover {
  text-decoration: underline;
}

@media (max-width: 768px) {
  .quick-nav {
    grid-template-columns: 1fr;
  }

  .stats-grid {
    grid-template-columns: repeat(2, 1fr);
  }

  .update-item {
    flex-direction: column;
    align-items: flex-start;
  }

  .update-date {
    margin-bottom: 0.5rem;
  }
}
